import React from 'react';
import logo from './logo.svg';
import './App.css';
import SignUp from './pages/SignUp';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.onSubmit = this.onSubmit.bind(this)
    this.state = {
      users: [],
      pageMap: {
        'signUp': <SignUp onSubmit={this.onSubmit}/>,
        'userDetail': <UserDetail/>,
        'usersList': <UserList users={this.state.users} />
      },
      currentPage: 'signUp',
    };
  }

  onSubmit(uname, email, pwd) {
    const newUser = { uname, email, pwd };
    this.setState(state => ({
      users: state.users.concat([newUser])
    }));
  }

  render() {
    let currentPage = this.state.pageMap[this.state.currentPage];
    return (
      <div className="App">
        {currentPage}
      </div>
    );
  }
}

export default App;
