import React, {Component} from 'react';

class signUp extends Component {
  constructor(props) {
    super(props);
    this.state = {
      uname: '',
      email: '',
      pwd: '',
      showPwd: false,
    };
  }

  updateField(e, field) {
    console.log(e);
    this.setState({ [field]: e.target.value });
  }

  render() {
    const { uname, email, pwd } = this.state;
    return (
      <form onSubmit={() => { this.props.submitForm(uname, email, pwd) }}>
        <label htmlFor="uname">Username:</label><br/>
        <input type="text" id="uname" value={uname} autoComplete="off"
          onChange={(event) => { this.updateField(event, 'uname') }}/><br/>
        <label htmlFor="lname">Email:</label><br/>
        <input type="email" id="email" value={email}
          onChange={(event) => { this.updateField(event, 'email') }}
        /><br/>
        <label htmlFor="lname">Password:</label><br/>
        <input type={this.showPwd ? 'text' : 'password'} id="password" value={pwd}
          onChange={(event) => { this.updateField(event, 'pwd') }}
        /><br/><button onClick={(e)=>{e.preventDefault(); this.showPwd = !this.showPwd;}}>Show</button>
        <input type="submit" value="Submit"/>
      </form>
    );
  }
}

export default signUp;
