import React, {Component} from 'react';

class signUp extends Component {
  constructor(props) {
    super(props);
    this.state = {
      uname: '',
      email: '',
      pwd: '',
    };
  }

  render() {
    const { uname, email, pwd } = this.state;
    return (
      <form onSubmit={() => { this.props.submitForm(uname, email, pwd) }}>
        <label for="uname">Username:</label><br/>
        <input type="text" id="uname" value={uname} onChange={this.setUname}/><br/>
        <label for="lname">Email:</label><br/>
        <input type="email" id="email" value={email} onChange={this.setEmail}/><br/>
        <label for="lname">Password:</label><br/>
        <input type="password" id="email" value={password} onChange={this.setPwd}/><br/>
        <input type="submit" value="Submit"/>
      </form>
    );
  }
}

export default signUp;
