import React from 'react';
import logo from './logo.svg';
import './App.css';
import SignUp from './pages/SignUp';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.onSubmit = this.onSubmit.bind(this)
    this.state = {
      users: [],
      user: {
        uname: '',
        email: '',
        pwd: '',
      },
      currentPage: 'signUp',
    };
  }

  onSubmit(uname, email, pwd) {
    const newUser = { uname, email, pwd };
    this.setState(state => ({
      users: state.users.concat([newUser]),
      currentPage: '',
    }));
  }

  fetchCurrentPage() {
    const currPage = this.state.currentPage;
    if (currPage === 'signUp') {
      return <SignUp onSubmit={this.onSubmit} userDetail={this.state.user}/>;
    } else if (currPage === 'userDetail') {
      // return <UserDetail/>;
    } else if (currPage === 'usersList') {
      // return <UserList users={this.state.users} />;
    }
  }

  render() {
    let currentPage = this.fetchCurrentPage();
    return (
      <div className="App">
        <button></button> "Sign Up"/><button value="View Registrations"/><button value="Dashboard"></button>
        {currentPage}
      </div>
    );
  }
}

export default App;
