import React, {Component} from 'react';

class signUp extends Component {
  constructor(props) {
    super(props);
    this.state = {
      uname: '',
      email: '',
      pwd: '',
    };
  }

  setUname(e) {
    this.setState({ uname: e.target.value });
  }

  setEmail(e) {
    this.setState({ email: e.target.value });
  }
  render() {
    const { uname, email, pwd } = this.state;
    return (
      <form onSubmit={() => { this.props.submitForm(uname, email, pwd) }}>
        <label for="uname">Username:</label><br/>
        <input type="text" id="uname" value={uname}
          onChange={(event) => { this.updateField(event, 'uname') }}/><br/>
        <label for="lname">Email:</label><br/>
        <input type="email" id="email" value={email}
          onChange={(event) => { this.updateField(event, 'email') }}
        /><br/>
        <label for="lname">Password:</label><br/>
        <input type="password" id="email" value={pwd}
          onChange={(event) => { this.updateField(event, 'pwd') }}
        /><br/>
        <input type="submit" value="Submit"/>
      </form>
    );
  }
}

export default signUp;
