import React from 'react';
import logo from './logo.svg';
import './App.css';
import SignUp from './pages/SignUp';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      users: [],
    };
  }

  onSubmit({ uname, email, pwd }) {
    const newUser = { uname, email, pwd };
    this.setState(state => {
      users: state.users.concat([newUser])
    });
  }

  render() {
    return (
      <div className="App">
        <SignUp />
      </div>
    );
  }
}

export default App;
