import React from 'react';
import './App.css';
import SignUp from './pages/SignUp';
import UserDetail from './pages/UserDetail';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.onSubmit = this.onSubmit.bind(this)
    this.state = {
      users: [],
      user: {
        uname: '',
        email: '',
        pwd: '',
      },
      currentPage: 'signUp',
    };
  }

  formSubmit(uname, email, pwd) {
    const newUser = { uname, email, pwd };
    this.setState(state => ({
      user: newUser,
      currentPage: 'userDetail',
    }));
  }

  registerUser(name, address, contact) {
    const user = Object.assign({}, { name, address, contact }, this.state.user);
    this.setState({
      users: this.state.users.concat([{user}]),
      currentPage: 'usersList',
    })
  }

  fetchCurrentPage() {
    const currPage = this.state.currentPage;
    if (currPage === 'signUp') {
      return <SignUp onSubmit={this.formSubmit} userDetail={this.state.user}/>;
    } else if (currPage === 'userDetail') {
      return <UserDetail userDetail={this.state.user} onSubmit={this.registerUser}/>;
    } else if (currPage === 'usersList') {
      // return <UserList users={this.state.users} />;
    }
    // return <DashBoard/>;
  }

  changePage(newPage) {
    this.setState({ currentPage: newPage });
  }

  render() {
    let currentPage = this.fetchCurrentPage();
    return (
      <div className="App">
        <button onClick={()=> {this.changePage('signUp')}}>Sign Up</button>
        <button onClick={()=> {this.changePage('usersList')}}>View Registrations</button>
        <button onClick={()=> {this.changePage('dashboard')}}>Dashboard</button>
        {currentPage}
      </div>
    );
  }
}

export default App;
